import axios from 'axios';
import headtitle from "../title.vue";
import toast from '@/components/toast';
import { constants } from 'fs';
import * as apiUrl from "@/common/apiUrl.js";
export default {
  name: "comment",
  components: {
    headtitle: headtitle,
    toast: toast
  },
  data: function data() {
    return {
      back: true,
      title: '评论',
      userInfo: '',
      toastOptions: {},
      param: {
        videoId: '',
        userId: '',
        userNickName: '',
        content: ''
      }
    };
  },
  mounted: function mounted() {
    this.param.videoId = this.$route.query.id;
    this.getUserInfo();
  },
  computed: {
    wordNum: function wordNum() {
      return this.param.content.length;
    }
  },
  methods: {
    getUserInfo: function getUserInfo() {
      this.userInfo = JSON.parse(this.$store.state.userinfo);
      this.param.userId = this.userInfo.userId, this.param.userNickName = this.userInfo.userName;
    },
    onSaveComment: function onSaveComment() {
      var _this = this;

      if (this.param.content == '' || this.param.content.length > 30) {
        this.showToast({
          content: "请输入评论内容"
        });
        return;
      }

      console.log(this.param);
      axios.put(apiUrl.api_test_url + 'lnVideo/saveComments', {
        userId: this.param.userId,
        videoId: this.param.videoId,
        userNickName: this.param.userNickName,
        content: this.param.content
      }).then(function (res) {
        if (res.data.success) {
          _this.showToast({
            content: "发布评论成功",
            callback: function callback() {
              _this.$router.go(-1);
            }
          });

          _this.param.content = '';
        }
      }).catch(function (res) {
        console.log(res);
      });
    },
    showToast: function showToast(obj) {
      this.$refs.toast.showTips();
      this.toastOptions = obj;
    }
  }
};
