import axios from 'axios';
import { constants } from 'fs';
import myDialog from '@/components/confirm.vue';
import * as apiUrl from "@/common/apiUrl.js";
export default {
  name: "search",
  components: {
    // headtitle
    myDialog: myDialog
  },
  data: function data() {
    return {
      placeholder: '搜索视频',
      query: '',
      queryResult: [],
      hasResult: false,
      page: 1,
      size: 2,
      seaHis: [],
      userInfo: {},
      confirmOptions: {}
    };
  },
  watch: {
    query: function query() {
      var _this = this;

      if (this.query == '') {
        this.hasResult = false;
        this.queryResult = [];
        return;
      }

      axios.get(apiUrl.api_test_url + 'lnVideo/list/' + this.page + '/' + this.size, {
        params: {
          videoName: this.query
        }
      }).then(function (res) {
        _this.queryResult = [];
        var data = res.data;
        _this.hasResult = true;
        var list = data.queryResult.list;
        var resList = _this.queryResult;

        for (var key in list) {
          resList.push(list[key]);
        }

        _this.queryResult = resList;
      }).catch(function (err) {
        console.log(err);
      });
    }
  },
  mounted: function mounted() {
    if (this.$store.state.userinfo) {
      this.userInfo.userId = JSON.parse(this.$store.state.userinfo).userId;
      this.initHistory(this.userInfo.userId);
    } else {
      this.initHistory('tourists');
    }
  },
  methods: {
    initHistory: function initHistory(userId) {
      var seaHis = window.sessionStorage.getItem('seaHis') ? JSON.parse(window.sessionStorage.getItem('seaHis')) : '';

      if (seaHis == '' || seaHis == 'undefined') {
        return;
      } else {
        this.showHistory(userId);
      }
    },
    toDetail: function toDetail(videoId) {
      var obj = {};

      if (this.userInfo.userId) {
        //userId存在，说明用户已登录
        obj = {
          userId: this.userInfo.userId,
          name: this.query
        };
      } else {
        //userId不存在，说明用户是游客状态
        obj = {
          userId: 'tourists',
          name: this.query
        };
      } //点击搜索结果时判断是否存储搜索记录


      var seaHis = JSON.parse(window.sessionStorage.getItem('seaHis')) || ''; //定义是否存储状态 true：存储 

      var isStorage = true;
      var arr = [];

      for (var key in seaHis) {
        //搜索记录相同时 存储状态改为false
        if (seaHis[key].name == obj.name) {
          isStorage = false;
          break;
        }

        arr.push(seaHis[key]);
      } //根据存储状态来判断是否存储


      if (isStorage) {
        arr.unshift(obj);
        window.sessionStorage.setItem('seaHis', JSON.stringify(arr));
      }

      this.$router.push({
        path: '/detail',
        query: {
          id: videoId
        }
      });
    },
    showHistory: function showHistory(userId) {
      // console.log('showHistory  id= ' + userId)
      var searchHistory = JSON.parse(window.sessionStorage.getItem('seaHis'));
      var arr = [];

      for (var key in searchHistory) {
        if (searchHistory[key].userId === userId) {
          arr.push(searchHistory[key]);
        }
      }

      this.seaHis = arr;
    },
    historyHandler: function historyHandler(name) {
      this.query = name;
    },
    delHistory: function delHistory() {
      var _this2 = this;

      this.$refs.myConfirm.showModel();
      this.confirmOptions = {
        type: "warning",
        //warning
        message: "是否删除搜索历史？",
        //""
        btnSubmitFunction: function btnSubmitFunction() {
          window.sessionStorage.removeItem('seaHis');

          _this2.showHistory();
        },
        btnCancelFunction: function btnCancelFunction() {}
      };
    },
    onCancle: function onCancle() {
      this.$router.go(-1);
    }
  }
};
