      }, {
        image: 'http://39.96.201.228:4869/18a22e79b05ef34b3b8017fc5f6d06b7'
      }]
    };
  },
  created: function created() {
    this.isload = true;
    this.scrollToEnd();
  },
  methods: {
    scrollToEnd: function scrollToEnd() {
      var _this = this;

      if (this.videos.length >= this.count) {
        this.hasMore = false;
        return;
      }

      axios.get(apiUrl.api_test_url + 'lnVideo/list/' + this.page + '/' + this.size).then(function (res) {
        var data = res.data;

        if (data.success) {
          var list = data.queryResult.list;
          _this.page++;
          _this.videos = _this.videos.concat(list);
        }

        _this.isload = false;
      }).catch(function (err) {
        console.log(err);
      });
    },
