import Vue from "vue";
import Router from "vue-router";
import index from "./views/index/index.vue";
import login from "./views/mine/login.vue"
import outlogin from "./views/mine/outlogin.vue"
// import indexdetail from "./views/index/detail.vue";
// import center from './views/mine/center.vue'

Vue.use(Router);

export default new Router({
  mode: "history",
  base: process.env.BASE_URL,
  routes: [
    {
      path: "/",
      name: "index",
      component: index,
      meta:{
        keepAlive:true,
        isLogin: true
      }
    },{
      path: "/detail",
      name: "indexdetail",
      component: () =>
        import( "./views/index/detail.vue"),
      meta:{
        needLogin: true,
        keepAlive: false
      }
    },
    {
      path: "/center",
      name: "center",
      // route level code-splitting
      // this generates a separate chunk (about.[hash].js) for this route
      // which is lazy-loaded when the route is visited.
      component: () =>
        import(/* webpackChunkName: "about" */ "./views/mine/center.vue"),
      meta:{
        keepAlive: true
      }
    }, 
    {
      path: "/test",
      name: "test",
      component: () =>
        import("./views/test.vue"),
      meta:{
        keepAlive: true
      }
    },
    {
      path: "/login",
      // name: "login",
      component: login,
      meta:{
        isLogin: false
      }
    },
    {
      path: "/outlogin",
      // name: "login",
      component: outlogin,
      meta:{
        isLogin: false
      }
    },
    // {
    //   path: '*', component: NotFoundComponent
    // }
  ]
});
