.index-page {
  padding-top: 40px;
  .wrapper{ 
    position: absolute;
    top:50px;
    bottom: 50px;
    left:0;
    right:0;
    overflow: hidden;
    .slide-wrap {
      width: 100%;
      height: 150px;
      padding-bottom: 15px;
      overflow: hidden;
      margin-bottom: 25px;
      .cube-slide-item {
        padding: 0px 15px 10px;
        .slide-img {
          display: block;
          width: 100%;
          height: auto;
        }
      }
      .cube-slide-dots {
        bottom: -10px;
        height: 5px;
        .my-dot {
          width: 10px;
          height: 2px;
          margin-right: 10px;
          background: #ccc;
          &.active {
            background: #000;
            width: 20px;
          }
        }
      }
    }
    .list-wrap {
      padding: 0 15px 10px;
      box-sizing: border-box;
      .list-wrap-title {
        font-size: 18px;
        color: #000;
        padding: 10px 0 20px;
      }
      .list-box {
        position: relative;
        background-position: 50%;
        background-size: cover;
        color: #fff;
        padding-bottom: 25px;
        cursor: pointer;
        // height: 225px;
        .list-img {
          width: 100%;
          height: 180px;
          background-position: 50%;
          background-size: cover;
        }
        .list-title {
          padding: 15px 0px 8px;
          color: #1a1a1a;
          font-size: 14px;
          font-weight: 400;
        }
        .list-desc {
          line-height: 18px;
          font-size: 12px;
          color: #999;
          // padding-bottom: 25px;
          letter-spacing: 1px;
          display: -webkit-box;
          -webkit-box-orient: vertical;
          -webkit-line-clamp: 2; //需要控制的文本行数
          overflow: hidden;
          // font-weight: 300;
          // word
        }
      }
    }
  }
}
