.center-page{ 
    background: #fff;
    overflow: hidden;
    .userinfo-box{
        height: 200px;
        overflow: hidden;
        .userinfo-avatar{ 
            display:block;
            width:60px;
            height:60px;
            margin: 50px auto 30px;
            border-radius: 50%;
            overflow: hidden;
        }
        .usernickname{ 
            font-size: 14px; 
            text-align: center;
        }
        .usereditlink{
            position: relative;
            width:60px;
            margin-left:auto;
            margin-right: auto;
            .userNameEdit{
                position: absolute;
                bottom: 0;
                right:0;
                width: 20px;
            }
        }
    }
    .userinfo-list{
        padding: 0 20px;
        .userinfo-item{
            height: 40px;
            display: flex;
            align-items : center; 
            .userinfo-word{
                // height: 40px; 
                // line-height: 40px;
                margin-left: 15px;
            }
        }
    }
}
