result:
h1, p {
    line-height: 1.5;
}
.detail-wrap{   
    padding-top:40px;
    height: 100%;
    .detail-video-box{
        background: #000;
        height: 210px;
        overflow: hidden;
        position: relative;
        left: 50%;
        -webkit-transform: translate(-50%);
        transform: translate(-50%);
        width: 100%;
        video{    
            display: inline-block;
            *display: inline;
            *zoom: 1;
            height: 100%;
            position: absolute;
            width: 100%;
            left: 0;
            z-index: 1;
        }
        .cover{
            height: 100%;
            position: absolute;
            width: 100%;
            left: 0;
            background-position: bottom;
            background-repeat: no-repeat;
            background-size: cover;
            z-index: 2;
        }
        .cover.hide{ display: none;}
        .play-btn.hide{ display: none;}
        .play-btn{
            height: 100%;
            position: absolute;
            width: 100%;
            left: 0;
            background-color: rgba(0,0,0,.15);
            background-image: url(//static.kaiyanapp.com/eyepetizer-web/assets/images/detail/icon_play@2x.53ec01f2.png);
            background-position: 50%;
            background-repeat: no-repeat;
            background-size: 40px 40px;
            cursor: pointer;
            -webkit-transition: background-color .5s;
                    transition: background-color .5s;
            z-index: 3;
        }
    }
    .wrapper{
        background-image: url("http://img.kaiyanapp.com/4721aee1249d815abbc2834e4b8bd58a.png?imageMogr2/quality/60/format/jpg");
        background-color: rgba(0, 0, 0, .5);
        width: 100%;
        position: absolute;
        top: 250px;
        bottom: 40px;  /*关键*/
        overflow: hidden;
        z-index: 1;
        .content{

            overflow: hidden;
            .detail-video-info{
                color:#fff;
                .video-info-box{
                    // background: rgba(0,0,0,.2);
                    padding: 15px;
                    padding-bottom: 4px;
                    h1{
                        font-size: 20px;
                        margin: 0;
                        margin-bottom: 7px;
                    }
                    p{
                        margin: 0;
                        margin-bottom: 12px;
                    }
                    .vedio-desc {
                        margin-bottom: 11px;
                        font-weight: 300;
                        line-height: 20px;
                    }
                }
                .video-review-box{
                    // background: rgba(0,0,0,.4);
                    padding: 0;
                    overflow: hidden;
                    .header{
                        margin: 16px 0 10px;
                        text-align: center;
                    }
                    .video-review-list{
                        .review-item {
                            font-size: 12px;
                            padding-bottom: 15px;
                            position: relative;
                            .review-avatar{
                                background-position: 50%;
                                background-size: cover;
                                border-radius: 50%;
                                float: left;
                                height: 36px;
                                margin: 0 13px;
                                width: 36px;
                            }
                            .review-info-box{
                                margin-left: 62px;
                                .username{   
                                    margin: 0;
                                    margin-bottom: 4px;
                                }
                                .time{
                                    margin-bottom: 4px;
                                    opacity: .6;
                                }
                                .text{
                                    margin-bottom: 15px;
                                    padding-right: 35px;
                                }
                            }
                            .like{
                                position: absolute;
                                text-align: right;
                                top: 0;
                                right: 16px;
                                div{
                                    display: inline-block;
                                }
                                .like-icon{
                                    background-image: url(//static.kaiyanapp.com/eyepetizer-web/assets/images/detail/icon_like_grey@3x.d2a2a2a8.png);
                                    background-position: 50%;
                                    background-size: cover;
                                    height: 15px;
                                    width: 15px;
                                    margin-left:3px;
                                }
                            }
                        }
                        .noreview{
                            text-align: center;
                            padding: 15px 10px 30px;
                            font-size: 12px; 
                            opacity: .8;
                        }
                    }
                }

            }

        }
        // .video-cover-blurred {
            //     background-position: 50%;
            //     background-size: 100% 100%;
            //     height: 100%;
            //     position: absolute;
            //     top: 0;
            //     -webkit-transform: rotate(180deg) scaleX(-1);
            //             transform: rotate(180deg) scaleX(-1);
            //     width: 100%;
            //     z-index: -10;
        // }
    }
    .detail-footer{
        position: fixed;
        left:0;
        right:0;
        bottom:0;
        height: 40px;
        z-index:10;
        display: flex;
        width:100%;
        background: #333;
        color: #fff;
        // justify-items: center;
        justify-content : space-between;
        div{ 
            margin:0 30px;
            line-height: 30px;
            display: flex;
            flex-wrap: nowrap;
            justify-content: space-around;
            align-items: center;
            span{
                line-height: 16px;
                margin-top: 1px;
                margin-left: 2px;
            }
            .collect-icon{
                width:20px;
                height: 20px;
                // display: inline-block;
                // margin-top:3px;
            }
        }

    }

}
