import { constants } from 'fs';
export default {
/**
 * toast 模态接口参数
 * @param {string} modal.content 模态框提示内容
 * @param {function} modal.callback 模态框是否有回调方法
 */
  name: "toast",
  data: function(){
      return {
          show: false
      }
  },
  props:{
     toastOptions: Object,
  },
  computed:{
      tips:{
          get(){
              let tips = this.toastOptions || {};
              tips = {
                  content: tips.content || '操作成功',
                  callback: tips.callback || function(){},
                  time: tips.time || 1500
              };
              return tips;
          }
      }
  },
  methods:{
    showTips(){
        this.show = true;
        setTimeout(()=>{
            // this.$emit('callback', this.tips.callback); 
            this.show = false;
            this.tips.callback();
        }, this.tips.time)
    }
  }
};
